import 'package:flutter/material.dart';
import '../constants.dart';
import '../controllers/link_controller.dart';
import '../controllers/user_controller.dart';
import '../models/link.dart';
import '../models/user.dart';
import 'package:auto_size_text/auto_size_text.dart';

class FriendProfileView extends StatefulWidget {
  static String id = '/friendProfileView';
  
  const FriendProfileView({super.key});

  @override
  State<FriendProfileView> createState() => _FriendProfileViewState();
}

class _FriendProfileViewState extends State<FriendProfileView> {
  late Future<User> user;
  late Future<List<Link>> links;

  @override
  void initState() {
    user = getLocalUser();
    links = getLinks(context);
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
        body: Padding(
      padding: const EdgeInsets.only(top: 50, left: 16, bottom: 0, right: 16),
      child: Center(
        child: Column(children: [
          Row(
            mainAxisAlignment: MainAxisAlignment.start,
            children: [
              IconButton(
                onPressed: () {
                  Navigator.pop(context);
                },
                icon: const Icon(
                  Icons.arrow_back_ios_rounded,
                  color: kPrimaryColor,
                  size: 40,
                ),
              ),
              const SizedBox(
                width: 60,
              ),
              const Text(
                'friend name',
                style: TextStyle(
                  fontSize: 30,
                  color: kPrimaryColor,
                  fontWeight: FontWeight.bold,
                ),
              ),
            ],
          ),
          const SizedBox(
            height: 20,
          ),
          Container(
            decoration: BoxDecoration(
                color: kPrimaryColor, borderRadius: BorderRadius.circular(12)),
            child: Padding(
              padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 16),
              child: Column(children: [
                Row(crossAxisAlignment: CrossAxisAlignment.start, children: [
                  const CircleAvatar(
                      radius: 50,
                      backgroundImage: AssetImage('assets/imgs/friend.png')),
                  const SizedBox(
                    width: 20,
                  ),
                  Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        FutureBuilder(
                          future: user,
                          builder: (context, snapshot) {
                            if (snapshot.hasData) {
                              return Text(
                                ' ${snapshot.data?.user?.name} ',
                                style: const TextStyle(
                                  color: Colors.white,
                                  fontWeight: FontWeight.bold,
                                  fontSize: 22,
                                ),
                              );
                            }
                            return const Text('@user');
                          },
                        ),
                        FutureBuilder(
                          future: user,
                          builder: (context, snapshot) {
                            if (snapshot.hasData) {
                              return AutoSizeText(
                                ' ${snapshot.data?.user?.email} ',
                                style: const TextStyle(
                                  color: Colors.white,
                                  fontWeight: FontWeight.w400,
                                  fontSize: 16,
                                ),
                                maxLines: 1,
                                minFontSize: 12,
                                maxFontSize: 16,
                              );
                            }
                            return const Text('@user');
                          },
                        ),
                        const SizedBox(
                          height: 8,
                        ),
                        InkWell(
                          onTap: () {},
                          child: Container(
                            padding: const EdgeInsets.symmetric(
                                horizontal: 16, vertical: 6),
                            decoration: BoxDecoration(
                              color: kSecondaryColor,
                              borderRadius: BorderRadius.circular(10),
                            ),
                            child: const Text(
                              'Follow',
                              style: TextStyle(
                                  color: kPrimaryColor,
                                  fontWeight: FontWeight.w500,
                                  fontSize: 18),
                            ),
                          ),
                        ),
                      ])
                ]),
              ]),
            ),
          ),
          const SizedBox(
            height: 16,
          ),
          SingleChildScrollView(
            child: SizedBox(
              height: 300,
              child: FutureBuilder(
                future: links,
                builder: (context, snapshot) {
                  if (snapshot.hasData) {
                    return ListView.separated(
                      itemCount: snapshot.data!.length,
                      padding: const EdgeInsets.all(12),
                      scrollDirection: Axis.vertical,
                      itemBuilder: (context, index) {
                        final title =
                            snapshot.data?[index].title?.toUpperCase();
                        final link = snapshot.data?[index].link;
                        final linkId = snapshot.data?[index].id;
                        if (index % 2 == 0) {
                          return Center(
                            child: Container(
                              decoration: BoxDecoration(
                                  color: kLightDangerColor,
                                  borderRadius: BorderRadius.circular(12)),
                              child: Container(
                                padding: const EdgeInsets.symmetric(
                                    horizontal: 16, vertical: 8),
                                width: MediaQuery.of(context).size.width * 0.85,
                                child: Column(
                                    crossAxisAlignment:
                                        CrossAxisAlignment.start,
                                    children: [
                                      const Text(
                                        'linkTitle',
                                        style: TextStyle(
                                            letterSpacing: 3,
                                            fontSize: 16,
                                            color: kOnLightDangerColor,
                                            fontWeight: FontWeight.w400),
                                      ),
                                      AutoSizeText(
                                        '$link',
                                        style: const TextStyle(
                                            color: kOnLightDangerColor,
                                            fontWeight: FontWeight.w400),
                                        maxLines: 1,
                                      )
                                    ]),
                              ),
                            ),
                          );
                        }
                        return Center(
                          child: Container(
                            decoration: BoxDecoration(
                                color: kLinksColor,
                                borderRadius: BorderRadius.circular(12)),
                            child: Container(
                              padding: const EdgeInsets.symmetric(
                                  horizontal: 16, vertical: 8),
                              width: MediaQuery.of(context).size.width * 0.85,
                              child: Column(
                                  crossAxisAlignment: CrossAxisAlignment.start,
                                  children: [
                                    const Text(
                                      'link',
                                      style: TextStyle(
                                          letterSpacing: 3,
                                          fontSize: 16,
                                          color: kPrimaryColor,
                                          fontWeight: FontWeight.w400),
                                    ),
                                    AutoSizeText(
                                      '$link',
                                      style: const TextStyle(
                                          color: kPrimaryColor,
                                          fontWeight: FontWeight.w400),
                                      maxLines: 1,
                                    )
                                  ]),
                            ),
                          ),
                        );
                      },
                      separatorBuilder: (context, index) {
                        return const SizedBox(
                          height: 16,
                        );
                      },
                    );
                  }
                  if (snapshot.hasError) {
                    return Text(snapshot.error.toString());
                  }
                  return const Center(
                    child: CircularProgressIndicator(
                      color: kPrimaryColor,
                    ),
                  );
                },
              ),
            ),
          ),
        ]),
      ),
    ));
  }
}
